SELECT EXISTS (
    SELECT 1
    FROM pg_roles
    WHERE rolname = 'wres'
);

SELECT EXISTS (
    SELECT 1
    FROM pg_roles
    WHERE rolname = 'wres_user'
);

SELECT rolcreaterole
FROM pg_roles
WHERE rolname = CURRENT_USER;

SELECT EXISTS (
    SELECT 1
    FROM information_schema.schemata
    WHERE catalog_name = 'wres'
        AND schema_name = 'wres'
);

SELECT EXISTS (
    SELECT 1
    FROM information_schema.schemata
    WHERE catalog_name = 'wres'
        AND schema_name = 'partitions'
);
