changeSet.sql:
create table wres.TimeSeriesTraceValue
(
    timeseriestrace_id bigint not null,
    valid_datetime timestamp with time zone not null,
    series_value double precision
) partition by range ( timeseriestrace_id );
comment on table wres.TimeSeriesTraceValue is 'Each row represents a value in a single timeseries trace.';
comment on column wres.TimeSeriesTraceValue.valid_datetime is 'The datetime for the value a.k.a. the valid time a.k.a. the time of the event.';
comment on column wres.TimeSeriesTraceValue.series_value is 'The value in this trace at this valid datetime.';
